(17, 79, 'PHP design patterns - Adapter design pattern', 'php-adapter-design-pattern', '<div>\r\n<pre class=\"prettyprint linenums\"><code>interface PaymentAdapter{\r\n	public function pay($amount);\r\n}\r\n\r\nclass PayPal{\r\n	public function __construct(){}\r\n	public function sendPayment($amount){\r\n		echo \"Paying via Paypal: \".$amount;\r\n	}\r\n}\r\n\r\nclass PaypalAdapter implements PaymentAdapter{\r\n	private $paypal;\r\n	public function __construct(Paypal $paypal){\r\n		$this->paypal = $paypal;\r\n	}\r\n	public function pay($amount){\r\n		$this->paypal->sendPayment($amount);\r\n	}\r\n}\r\n$paypal = new paypalAdapter(new PayPal());\r\n$paypal->pay(\'1234\');\r\n\r\n//Add new Adapter\r\nclass MoneyBooker{\r\n	public function __construct(){}\r\n	public function doPayment($amount){\r\n		echo \"Paying via MoneyBooker: \".$amount;\r\n	}\r\n}\r\n\r\nclass MoneyBookerAdapter implements PaymentAdapter{\r\n	private $moneyBooker;\r\n	public function __construct(MoneyBooker $moneyBooker){\r\n		$this->moneyBooker = $moneyBooker;\r\n	}\r\n	public function pay($amount){\r\n		$this->moneyBooker->doPayment($amount);\r\n	}\r\n}\r\n$moneyBooker = new MoneyBookerAdapter(new MoneyBooker());\r\n$moneyBooker->pay(\"345\");\r\n\r\n</code></pre>\r\n</div>', '', 1, 13, '2014-05-22 13:00:56', 262, '', '2015-09-16 01:06:57', 262, 262, '2015-09-16 01:06:57', '2014-05-22 13:00:56', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"0\",\"show_intro\":\"\",\"info_block_position\":\"\",\"show_category\":\"0\",\"link_category\":\"0\",\"show_parent_category\":\"0\",\"link_parent_category\":\"0\",\"sho