(22, 84, 'PHP design patterns - Command design pattern', 'php-command-design-pattern', '<pre class=\"prettyprint linenums\"><code>//Command\r\ninterface radioCommand{\r\n	public function excute();\r\n}\r\n//Receiver\r\nclass radioControl {\r\n	\r\n	public function turnOn(){\r\n		echo \"Radio Turn On\";\r\n	}\r\n	public function turnOff(){\r\n		echo \"Radio Turn Off\";\r\n	}\r\n	\r\n}\r\n\r\nclass turnOnRadio implements radioCommand {\r\n	\r\n	private $radioControl;\r\n	public function __construct(radioControl $radioControl){\r\n		$this-&gt;radioControl = $radioControl;\r\n	}\r\n	\r\n	public function excute(){\r\n		$this-&gt;radioControl-&gt;turnOn();\r\n	}	\r\n}\r\n\r\nclass turnOffRadio implements radioCommand {\r\n	private $radioControl;\r\n	public function __construct(radioControl $radioControl){\r\n		$this-&gt;radioControl = $radioControl;\r\n	}\r\n	public function excute(){\r\n		$this-&gt;radioControl-&gt;turnOff();\r\n	}\r\n}\r\n// Invoker\r\n$turnOn = \"turnOnRadio\";\r\nif(class_exists($turnOn)){\r\n	$command = new $turnOn(new radioControl());\r\n}\r\nelse {\r\n	throw new Exception(\"Command not found\");\r\n}\r\n$command-&gt;excute();\r\n\r\n</code></pre>', '', 1, 13, '2015-09-09 08:00:00', 262, '', '2015-09-17 16:03:04', 262, 262, '2016-10-15 03:35:34', '2015-09-09 08:00:00', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"show_category\":\"0\",\"link_category\":\"0\",\"show_parent_category\":\"0\",\"link_parent_category\":\"0\",\"show_author\":\"0\",\"link_author\":\"0\",\"show_create_date\":\"0\",\"show_modify_