e><code class=\\\"xml plain\\\">&lt;<\\/code><code class=\\\"xml keyword\\\">uses-sdk<\\/code><\\/div>\\r\\n<div class=\\\"line number10 index9 alt1\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml color1\\\">android:minSdkVersion<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"8\\\"<\\/code><\\/div>\\r\\n<div class=\\\"line number11 index10 alt2\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml color1\\\">android:targetSdkVersion<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"17\\\"<\\/code> <code class=\\\"xml plain\\\">\\/&gt;<\\/code><\\/div>\\r\\n<div class=\\\"line number12 index11 alt1\\\">&nbsp;<\\/div>\\r\\n<div class=\\\"line number13 index12 alt2\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml plain\\\">&lt;<\\/code><code class=\\\"xml keyword\\\">application<\\/code><\\/div>\\r\\n<div class=\\\"line number14 index13 alt1\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml color1\\\">android:allowBackup<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"true\\\"<\\/code><\\/div>\\r\\n<div class=\\\"line number15 index14 alt2\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml color1\\\">android:icon<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"@drawable\\/ic_launcher\\\"<\\/code><\\/div>\\r\\n<div class=\\\"line number16 index15 alt1\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml color1\\\">android:label<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"@string\\/app_name\\\"<\\/code><\\/div>\\r\\n<div class=\\\"line number17 index16 alt2\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml color1\\\">android:theme<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"@style\\/AppTheme\\\"<\\/code> <code class=\\\"xml plain\\\">&gt;<\\/code><\\/div>\\r\\n<div class=\\\"line number18 index17 alt1\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml plain\\\">&lt;<\\/code><code class=\\\"xml keyword\\\">activity<\\/code><\\/div>\\r\\n<div class=\\\"line number19 index18 alt2\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml color1\\\">android:name<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"com.example.androidpostdatatophpserver.MainActivity\\\"<\\/code><\\/div>\\r\\n<div class=\\\"line number20 index19 alt1\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml color1\\\">android:label<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"@string\\/app_name\\\"<\\/code> <code class=\\\"xml plain\\\">&gt;<\\/code><\\/div>\\r\\n<div class=\\\"line number21 index20 alt2\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml plain\\\">&lt;<\\/code><code class=\\\"xml keyword\\\">intent-filter<\\/code><code class=\\\"xml plain\\\">&gt;<\\/code><\\/div>\\r\\n<div class=\\\"line number22 index21 alt1\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml plain\\\">&lt;<\\/code><code class=\\\"xml keyword\\\">action<\\/code> <code class=\\\"xml color1\\\">android:name<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"android.intent.action.MAIN\\\"<\\/code> <code class=\\\"xml plain\\\">\\/&gt;<\\/code><\\/div>\\r\\n<div class=\\\"line number23 index22 alt2\\\">&nbsp;<\\/div>\\r\\n<div class=\\\"line number24 index23 alt1\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml plain\\\">&lt;<\\/code><code class=\\\"xml keyword\\\">category<\\/code> <code class=\\\"xml color1\\\">android:name<\\/code><code class=\\\"xml plain\\\">=<\\/code><code class=\\\"xml string\\\">\\\"android.intent.category.LAUNCHER\\\"<\\/code> <code class=\\\"xml plain\\\">\\/&gt;<\\/code><\\/div>\\r\\n<div class=\\\"line number25 index24 alt2\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml plain\\\">&lt;\\/<\\/code><code class=\\\"xml keyword\\\">intent-filter<\\/code><code class=\\\"xml plain\\\">&gt;<\\/code><\\/div>\\r\\n<div class=\\\"line number26 index25 alt1\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml plain\\\">&lt;\\/<\\/code><code class=\\\"xml keyword\\\">activity<\\/code><code class=\\\"xml plain\\\">&gt;<\\/code><\\/div>\\r\\n<div class=\\\"line number27 index26 alt2\\\"><code class=\\\"xml spaces\\\">&nbsp;&nbsp;&nbsp;&nbsp;<\\/code><code class=\\\"xml plain\\\">&lt;\\/<\\/code><code class=\\\"xml keyword\\\">application<\\/code><code class=\\\"xml plain\\\">&gt;<\\/code><\\/div>\\r\\n<div class=\\\"line number28 index27 alt1\\\">&nbsp;<\\/div>\\r\\n<div class=\\\"line number29 index28 alt2\\\"><code class=\\\"xml plain\\\">&lt;\\/<\\/code><code class=\\\"xml keyword\\\">manifest<\\/code><code class=\\\"xml plain\\\">&gt;<\\/code><\\/div>\\r\\n<\\/td>\\r\\n<\\/tr>\\r\\n<\\/tbody>\\r\\n<\\/table>\\r\\n<\\/div>\\r\\n<\\/div>\\r\\n<h2>Output Screenshots<\\/h2>\\r\\n<p>Response is what post_date_receiver.php outputs when it receives the posted data.<\\/p>\\r\\n<div><a href=\\\"http:\\/\\/www.codeofaninja.com\\/wp-content\\/uploads\\/2013\\/04\\/android-http-example-posting-text-data.jpg\\\"><img class=\\\"size-full wp-image-983\\\" src=\\\"http:\\/\\/www.codeofaninja.com\\/wp-content\\/uploads\\/2013\\/04\\/android-http-example-posting-text-data.jpg\\\" alt=\\\"android http example - posting text data output\\\" width=\\\"599\\\" height=\\\"409\\\" \\/><\\/a>\\r\\n<p class=\\\"wp-caption-text\\\">Posting Text Data. The response array have our key-value pairs.<\\/p>\\r\\n<\\/div>\\r\\n<div><a href=\\\"http:\\/\\/www.codeofaninja.com\\/wp-content\\/uploads\\/2013\\/04\\/android-http-example-posting-a-file.jpg\\\"><img class=\\\"size-full wp-image-984\\\" src=\\\"http:\\/\\/www.codeofaninja.com\\/wp-content\\/uploads\\/2013\\/04\\/android-http-example-posting-a-file.jpg\\\" alt=\\\"Posting Text File. The response is the content of my sample.txt file.\\\" width=\\\"584\\\" height=\\\"389\\\" \\/><\\/a>\\r\\n<p class=\\\"wp-caption-text\\\">Posting Text File. The response is the content of my sample.txt file.<\\/p>\\r\\n<\\/div>\\r\\n<p>What do you think fo this code I came up with? If you have a better solution about how to send data from Android to PHP server, please let us know in the comments section below! We will add your solution or update this post if it deserves to, thanks!<\\/p>\\r\\n<p style=\\\"text-align: right;\\\">&nbsp;<\\/p>\\r\\n<p style=\\\"text-align: right;\\\"><strong>&nbsp;Source:<\\/strong> <a href=\\\"http:\\/\\/www.codeofaninja.com\\/2013\\/04\\/android-http-client.html\\\">http:\\/\\/www.codeofaninja.com\\/2013\\/04\\/android-http-client.html<\\/a><\\/p>\",\"state\":1,\"catid\":\"9\",\"created\":\"2014-06-11 16:06:00\",\"created_by\":\"262\",\"created_by_alias\":\"\",\"modified\":\"2015-03-01 16:42:23\",\"modified_by\":\"262\",\"checked_out\":\"262\",\"checked_out_time\":\"2015-03-01 16:41:20\",\"publish_up\":\"2014-07-14 16:06:00\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_intro_alt\\\":\\\"\\\",\\\"image_intro_caption\\\":\\\"\\\",\\\"image_fulltext\\\":\\\"\\\",\\\"float_fulltext\\\":\\\"\\\",\\\"image_fulltext_alt\\\":\\\"\\\",\\\"image_fulltext_caption\\\":\\\"\\\"}\",\"urls\":\"{\\\"urla\\\":false,\\\"urlatext\\\":\\\"\\\",\\\"targeta\\\":\\\"\\\",\\\"urlb\\\":false,\\\"urlbtext\\\":\\\"\\\",\\\"targetb\\\":\\\"\\\",\\\"urlc\\\":false,\\\"urlctext\\\":\\\"\\\",\\\"targetc\\\":\\\"\\\"}\",\"attribs\":\"{\\\"show_title\\\":\\\"\\\",\\\"link_titles\\\":\\\"\\\",\\\"show_tags\\\":\\\"\\\",\\\"show_intro\\\":\\\"\\\",\\\"info_block_position\\\":\\\"\\\",\\\"show_category\\\":\\\"\\\",\\\"link_category\\\":\\\"\\\",\\\"show_parent_category\\\":\\\"\\\",\\\"link_parent_category\\\":\\\"\\\",\\\"show_author\\\":\\\"\\\",\\\"link_author\\\":\\\"\\\",\\\"show_create_date\\\":\\\"\\\",\\\"show_modify_date\\\":\\\"\\\",\\\"show_publish_date\\\":\\\"\\\",\\\"show_item_navigation\\\":\\\"\\\",\\\"show_icons\\\":\\\"0\\\",\\\"show_print_icon\\\":\\\"0\\\",\\\"show_email_icon\\\":\\\"0\\\",\\\"show_vote\\\":\\\"0\\\",\\\"show_hits\\\":\\\"0\\\",\\\"show_noauth\\\":\\\"\\\",\\\"urls_position\\\":\\\"\\\",\\\"alternative_readmore\\\":\\\"\\\",\\\"article_layout\\\":\\\"\\\",\\\"show_publishing_options\\\":\\\"\\\",\\\"show_article_options\\\":\\\"\\\",\\\"show_urls_images_backend\\\":\\\"\\\",\\\"show_urls_images_frontend\\\":\\\"\\\"}\",\"version\":30,\"ordering\":\"0\",\"metakey\":\"\",\"metadesc\":\"\",\"access\":\"1\",\"hits\":\"572\",\"metadata\":\"{\\\"robots\\\":\\\"\\\",\\\"author\\\":\\\"\\\",\\\"rights\\\":\\\"\\\",\\\"xreference\\\":\\\"\\\"}\",\"featured\":\"0\",\"language\":\"*\",\"xreference\":\"\"}', 0);
