(206, 18, 1, '', '2015-09-15 14:20:24', 262, 3510, 'd1f480b6260c8f340c4c0874c2fe3f58f7653625', '{\"id\":18,\"asset_id\":\"80\",\"title\":\"PHP design patterns - Factory design pattern\",\"alias\":\"php-factory-design-pattern\",\"introtext\":\"<div>\\r\\n<pre class=\\\"prettyprint linenums\\\"><code>abstract class Product{\\r\\n\\tprivate $sku;\\r\\n\\tprivate $name;\\r\\n\\tprotected $type = null;\\r\\n\\tpublic function __contruct($sku,$name){\\r\\n\\t\\t$this->sku  = $sku;\\r\\n\\t\\t$this->name = $name;\\r\\n\\t}\\r\\n\\tpublic function getSku(){\\r\\n\\t\\treturn $this->sku;\\r\\n\\t}\\r\\n\\tpublic function getName(){\\r\\n\\t\\treturn $this->name;\\r\\n\\t}\\r\\n\\tpublic function getType(){\\r\\n\\t\\treturn $this->type;\\r\\n\\t}\\r\\n}\\r\\n\\r\\nclass Product_Chair extends Product {\\r\\n\\tprotected $type = \'chair\';\\r\\n}\\r\\nclass Product_Table extends Product {\\r\\n\\tprotected $type = \'table\';\\r\\n}\\r\\nclass Product_Sofa extends Product {\\r\\n\\tprotected $type = \'sofa\';\\r\\n}\\r\\n\\r\\nclass ProductFactory{\\r\\n\\tpublic static function build($product_type, $sku, $name){\\r\\n\\t\\t$product = \\\"Product_\\\".ucwords($product_type);\\r\\n\\t\\tif(class_exists($product)){\\r\\n\\t\\t\\treturn new $product($sku,$name);\\r\\n\\t\\t}else{\\r\\n\\t\\t\\tthrow new Exception(\\\"Invalid product type\\\");\\r\\n\\t\\t}\\r\\n\\t}\\r\\n}\\r\\n\\r\\nclass ProductController{\\r\\n\\tpublic function create($product_type){\\r\\n\\t\\t$product = ProductFactory::build($product_type, $post[\'sku\'], $post[\'name\']);\\r\\n\\t\\treturn $product->getType();\\r\\n\\t}\\r\\n}\\r\\n\\/*\\r\\nclass ProductController {}\\r\\n\\tpublic function create($product_type){\\r\\n\\t\\tswitch($product_type){\\r\\n\\t\\t\\tcase \'chair\':\\r\\n\\t\\t\\t\\t$product = new Product_Chair($post[\'sku\'],$post[\'name\']);\\r\\n\\t\\t\\t\\tbreak;\\r\\n\\t\\t\\tcase \'table\':\\r\\n\\t\\t\\t\\t$product = new Product_Table($post[\'sku\'],$post[\'name\']);\\r\\n\\t\\t\\t\\tbreak;\\r\\n\\t\\t\\tcase \'sofa\':\\r\\n\\t\\t\\t\\t$product = new Product_Sofa($post[\'sku\'],$post[\'name\']);\\r\\n\\t\\t\\t\\tbreak;\\r\\n\\t\\t}\\r\\n\\t\\t\\/\\/ do some thing\\r\\n\\t\\treturn $product->getType();\\r\\n\\t}\\r\\n*\\/\\r\\n<\\/code><\\/pre>\\r\\n<\\/div>\",\"fulltext\":\"\",\"state\":1,\"catid\":\"13\",\"created\":\"2014-05-22 16:20:56\",\"created_by\":\"262\",\"created_by_alias\":\"\",\"modified\":\"2015-09-15 14:20:24\",\"modified_by\":\"262\",\"checked_out\":\"262\",\"checked_out_time\":\"2015-09-15 14:16:20\",\"publish_up\":\"2014-05-22 16:20:56\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_intro_alt\\\":\\\"\\\",\\\"image_intro_caption\\\":\\\"\\\",\\\"image_fulltext\\\":\\\"\\\",\\\"float_fulltext\\\":\\\"\\\",\\\"image_fulltext_alt\\\":\\\"\\\",\\\"image_fulltext_caption\\\":\\\"\\\"}\",\"urls\":\"{\\\"urla\\\":false,\\\"urlatext\\\":\\\"\\\",\\\"targeta\\\":\\\"\\\",\\\"urlb\\\":false,\\\"urlbtext\\\":\\\"\\\",\\\"targetb\\\":\\\"\\\",\\\"urlc\\\":false,\\\"urlctext\\\":\\\"\\\",\\\"targetc\\\":\\\"\\\"}\",\"attribs\":\"{\\\"show_title\\\":\\\"\\\",\\\"link_titles\\\":\\\"\\\",\\\"show_tags\\\":\\\"\\\",\\\"show_intro\\\":\\\"\\\",\\\"info_block_position\\\":\\\"\\\",\\\"show_category\\\":\\\"0\\\",\\\"link_category\\\":\\\"0\\\",\\\"show_parent_category\\\":\\\"0\\\",\\\"link_parent_category\\\":\\\"0\\\",\\\"show_author\\\":\\\"0\\\",\\\"link_author\\\":\\\"0\\\",\\\"show_create_date\\\":\\\"0\\\",\\\"show_modify_date\\\":\\\"0\\\",\\\"show_publish_date\\\":\\\"0\\\",\\\"show_item_navigation\\\":\\\"0\\\",\\\"show_icons\\\":\\\"0\\\",\\\"show_print_icon\\\":\\\"0\\\",\\\"show_email_icon\\\":\\\"0\\\",\\\"show_vote\\\":\\\"0\\\",\\\"show_hits\\\":\\\"0\\\",\\\"show_noauth\\\":\\\"\\\",\\\"urls_position\\\":\\\"\\\",\\\"alternative_readmore\\\":\\\"\\\",\\\"article_layout\\\":\\\"\\\",\\\"show_publishing_options\\\":\\\"\\\",\\\"show_article_options\\\":\\\"\\\"